mapper.update:
UPDATE FREEBOARD
		SET FREE_TITLE = #{freeTitle},
			FREE_CONTENT = #{freeContent}

	WHERE FREE_NO = #{freeNo}

mapper.select:
SELECT *
FROM FREEGOOD
WHERE RMEM_NO = #{memNo}
AND RFREE_NO = #{bno}

SELECT FREE_GOOD_STATUS
FROM FREEGOOD
WHERE RMEM_NO = #{rmemNo}
AND RFREE_NO= #{rfreeNo}

