mapper.select:
SELECT COUNT(*)
FROM STORE
WHERE BUSINESS_NO = #{businessNo}

SELECT COUNT(*)
FROM STORE
WHERE LOCATION_BIG = #{locationBig}

AND LOCATION_SMALL = #{locationSmall}

AND STORE_STATUS = 'open'

SELECT s.STORE_NO, s.STORE_ADDRESS, s.STORE_NAME, COUNT(DISTINCT CASE WHEN g.STORE_GOOD_STATUS = 'Y' THEN g.RSTORE_NO END) AS like_count, COALESCE(MIN(t.TICKET_PRICE), 0) AS min_price, COUNT(DISTINCT CASE WHEN r.REPLY_STATUS = 'Y' THEN r.REPLY_NO END) AS reply_count, CHANGE_NAME FROM STORE s LEFT JOIN STOREGOOD g ON s.STORE_NO = g.RSTORE_NO LEFT JOIN TICKET t ON s.STORE_NO = t.RSTORE_NO LEFT JOIN REPLY r ON s.STORE_NO = r.RSTORE_NO LEFT JOIN ATTACHMENT a ON s.STORE_NO = a.RSTORE_NO AND a.FILE_LEVEL = 1 WHERE (STORE_KIND = 'FreshSeat' OR STORE_KIND = 'FreshCafe') AND s.LOCATION_BIG = #{locationBig}
AND s.LOCATION_SMALL = #{locationSmall}
AND s.STORE_STATUS = 'open' GROUP BY s.STORE_NO, s.STORE_ADDRESS, s.STORE_NAME, CHANGE_NAME

SELECT *
FROM TICKET
WHERE RSTORE_NO = #{rstoreNo}

 		SELECT *
		FROM STOREGOOD
		WHERE RMEM_NO = #{memNo}
		AND RSTORE_NO = #{storeNum}

 		SELECT STORE_GOOD_STATUS
		FROM STOREGOOD
		WHERE RMEM_NO = #{rmemNo}
		AND RSTORE_NO = #{rstoreNo}

mapper.update:
 		UPDATE STOREGOOD
		SET STORE_GOOD_STATUS = #{result}
		WHERE RSTORE_NO = #{rstoreNo}
		AND RMEM_NO = #{rmemNo}

mapper.select:
			SELECT COUNT(*)
			FROM (SELECT *
                    FROM STORE
                    WHERE STORE_KIND = 'seaShip'
                    OR STORE_KIND = 'seaSeat')
            WHERE LOCATION_SMALL = #{city1}
            OR LOCATION_SMALL = #{city2}
            OR LOCATION_SMALL = #{city3}
            OR LOCATION_SMALL = #{city4}
            OR LOCATION_SMALL = #{city5}
            OR LOCATION_SMALL = #{city6}
            AND STORE_STATUS = 'open'

			SELECT COUNT(*)
			FROM (SELECT *
                    FROM STORE

                    WHERE STORE_KIND = 'seaShip'


                    WHERE STORE_KIND = 'seaSeat'


                    WHERE STORE_KIND = 'seaShip'
                    OR STORE_KIND = 'seaSeat'

                    )
            WHERE LOCATION_SMALL = #{city1}
            OR LOCATION_SMALL = #{city2}
            OR LOCATION_SMALL = #{city3}
            OR LOCATION_SMALL = #{city4}
            OR LOCATION_SMALL = #{city5}
            OR LOCATION_SMALL = #{city6}
            AND STORE_STATUS = 'open'

SELECT RESERVATION_DETAIL
FROM STORE
WHERE STORE_NO = #{storeNum}

mapper.update:
UPDATE STORE
SET reservation_detail = #{info}
WHERE STORE_NO = #{storeNum}

mapper.select:
SELECT *
FROM TICKET
WHERE TICKET_NO = #{ticketNo}

mapper.update:
UPDATE ATTACHMENT
SET ORIGIN_NAME = #{originName},
	CHANGE_NAME = #{changeName},
	UPLOAD_DATE = SYSDATE,
	FILE_LEVEL = #{fileLevel}
WHERE RSTORE_NO = #{rstoreNo} AND FILE_NO = #{fileNo}

UPDATE STORE
SET STORE_STATUS = #{storeStatus}
WHERE STORE_NO = #{storeNo}

UPDATE STORE
SET STORE_STATUS = 'DeleteOpen'
WHERE BUSINESS_NO = #{businessNo}
